var items = $contentText.value.split(/[\n\r]+/);
var specs = [];
for (var i = 0; i < items.length; i ++) {
  var css = new CSS();

  var title = items[i];

  if(title.match(/\S/) != null) {
    var lineHeight = ((i+0) * 46) + 30;
    var boxOrigin= (i) * 46;

    var css = new CSS();
    css.set("font-size",$itemFont.size);
    css.set("font-family",$itemFont.family);
    css.set("font-style",$itemFont.style);
    css.set("font-weight",$itemFont.weight);
    css.set("font-decor",$itemFont.decor);
    css.set("fill", $itemColor.toRGBString());

    specs.push({
      	_name: "rect",
      	_uri: "http://www.w3.org/2000/svg",
      	x: 0,
      	y: boxOrigin + 1 + $handleList.y,
      	width: $box.w,
      	height: 46,
      	style: "stroke:none; fill:#393939"
      },{
        _name: "text",
        _uri: "http://www.w3.org/2000/svg",
        x: 52,
        y: lineHeight + 1 + $handleList.y,
        _text : title,
        style: css
      },{
      	_name: "rect",
      	_uri: "http://www.w3.org/2000/svg",
      	x: 0,
      	y: boxOrigin + 1 + $handleList.y,
      	width: $box.w,
      	height: 1,
      	style: "stroke:none; fill:#4d4d4d"
      },{
      	_name: "rect",
      	_uri: "http://www.w3.org/2000/svg",
      	x: 0,
      	y: (boxOrigin + 46 + $handleList.y),
      	width: $box.w,
      	height: 1,
      	style: "stroke:none; fill:#2b2b2b"
      });
  }
}
Dom.newDOMFragment(specs);
